-- Create Database
CREATE DATABASE `sms_based_quiz_demo`;

USE sms_based_quiz_demo;

-- Table structure for table `users`
CREATE TABLE `users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `msisdn` char(15) NOT NULL,
  `registration_date` datetime NOT NULL,
  `user_status` char(1) NOT NULL DEFAULT '1',
  `last_activity_date` datetime NOT NULL,
  `last_question_id` int(11) NOT NULL DEFAULT '0',
  `user_score` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `msisdn_UNIQUE` (`msisdn`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Table structure for table `activity_log`
CREATE TABLE `activity_log` (
  `activity_id` int(11) NOT NULL AUTO_INCREMENT,
  `msisdn` char(15) NOT NULL,
  `text_msg` varchar(500) DEFAULT NULL,
  `activity_date` datetime NOT NULL,
  PRIMARY KEY (`activity_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Table structure for table `questions`
CREATE TABLE `questions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `question_id` int(11) NOT NULL,
  `full_question` varchar(500) NOT NULL,
  `word_count_full_question` int(11) NOT NULL,
  `question_english` varchar(300) NOT NULL,
  `question_bangla` varchar(500) NOT NULL,
  `word_count_bangla` int(11) NOT NULL,
  `option_a` varchar(100) NOT NULL,
  `option_a_bangla` varchar(200) NOT NULL,
  `option_b` varchar(100) NOT NULL,
  `option_b_bangla` varchar(200) NOT NULL,
  `answer` char(1) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `rjc_questionscol_UNIQUE` (`question_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

